package com.biz.test_1106;

public class PostfixOp {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 5;
		System.out.print((num++) + " ");
		System.out.print((num++) + " ");
		System.out.print(num + "\n");
		
		System.out.print((num--) + " ");
		System.out.print((num--) + " ");
		System.out.print(num);

	}

}
